      status:'success',
      title:'Success',
      message:'Sent cart data succesfully'
    }))
  }
  sendCartData().catch(error=>{
    dispatch(uiAction.showNotification({
      status:'error',
      title:'Error',
      message:'Sending cart data failed'
    }))
  })
  },[cart,dispatch])
  console.log(isCartShown);
  console.log(notification);
  return (
    <>
   {notification &&  <Notification />}
